import { Link, useNavigate } from 'react-router-dom';
import { useAuth } from './Context/AuthContext';
import styles from '../styles/Plans.module.css';
import BrainIco from '../images/ico/BrainIco.png';

export default function Plans() {
  const { isAuthenticated, logout } = useAuth();
  const navigate = useNavigate();

  const handleLogout = () => {
    logout();
    navigate('/');
  };

  return (
    <div className={styles.pageWrapper}>
      <header className={styles.header}>
        <div className={styles.logoArea}>
          <img src={BrainIco} alt="Ícone Brainboost" className={styles.headerIcon} />
          <h1 className={styles.logo}><a href="/">Brainboost</a></h1>
        </div>
        {isAuthenticated ? (
          <button onClick={handleLogout} className={styles.loginBtn}>Logout</button>
        ) : (
          <Link to="/login" className={styles.loginBtn}>Login</Link>
        )}
      </header>

      <main className={styles.mainContent}>
        <h2>Escolha o Plano que se Adequa às Suas Necessidades</h2>

        <div className={styles.planContainer}>
          <div className={styles.planCard}>
            <h3>Plano Gratuito</h3>
            <p>Acesso limitado às funcionalidades</p>
            <p>Resumos de vídeos até 5 minutos</p>
            <p>Máximo de 3 vídeos por semana</p>
            <button onClick={() => navigate('/register')} className={styles.planButton}>Assine Grátis</button>
          </div>

          <div className={styles.planCardPremium}>
            <h3>Plano Premium</h3>
            <p>Acesso completo às funcionalidades</p>
            <p>Resumos ilimitados e personalizados</p>
            <p>Geração de questões interativas</p>
            <button className={styles.planButton}>Assinar Premium</button>
          </div>

          <div className={styles.planCard}>
            <h3>Já possui uma conta?</h3>
            <p>Entre agora para acessar seus resumos e questões.</p>
            <button onClick={() => navigate('/login')} className={styles.planButton}>Login</button>
          </div>
        </div>
      </main>
    </div>
  );
}
