import BrainIco from 'assets/images/ico/BrainIco.png';
import img1 from 'assets/images/img1.jpeg';
import bgGreen from 'assets/images/Background-green.jpg';
import CheckIco from 'assets/images/ico/CheckIco.svg';
import BookIco from 'assets/images/ico/BookIco.png';
import BookPcIco from 'assets/images/ico/BookPcIco.png';
import PcIco from 'assets/images/ico/PcIco.png';

import img2 from 'assets/images/img2.jpeg';
import img3 from 'assets/images/img3.jpeg';
import img4 from 'assets/images/img4.jpeg';
import img5 from 'assets/images/img5.jpeg';
import waitUp from 'assets/animations/waitup.gif';

import { useEffect, useState } from 'react';
import ScrollReveal from 'scrollreveal';
import { Link, useNavigate } from 'react-router-dom';

import UrlForm from 'components/UrlForm/urlForm';
import { useAuth } from 'context/AuthContext';
import { submitVideo } from 'services/videoService';

export default function Home() {
  useEffect(() => {
    ScrollReveal().reveal('.header', { duration: 2000 });
    ScrollReveal().reveal('.hero-image', {
      origin: 'left',
      duration: 2000,
      distance: '10%',
    });
    ScrollReveal().reveal('.hero-content', {
      origin: 'right',
      duration: 2000,
      distance: '10%',
    });
  }, []);

  const [isLoading, setIsLoading] = useState(false);
  const navigate = useNavigate();
  const { isAuthenticated, logout } = useAuth();

  const handleLogout = () => {
    logout();
    navigate('/');
  };

  async function onSubmit(url: string) {
    if (!isAuthenticated) {
      navigate('/plans');
      return;
    }

    setIsLoading(true);
    try {
      const result = await submitVideo(url);
      localStorage.setItem('video_data', JSON.stringify(result));
      setIsLoading(false);
      navigate('/questions');
    } catch (err: any) {
      console.error('Erro ao processar vídeo:', err);
      alert('Erro ao processar o vídeo. Verifique o link e tente novamente.');
      setIsLoading(false);
    }
  }

  return (
    <div>
      {isLoading && (
        <div className="waiting">
          <img src={waitUp} alt="Carregando..." style={{ width: 100, height: 100 }} />
        </div>
      )}

      <header className="header">
        <div className="header-container">
          <h3 className="logo">
            <img className="header-icon" src={BrainIco} alt="Ícone" width={64} height={64} />
            <a href="/">Brainboost</a>
          </h3>

          <nav className="navbar">
            <ul>
              <li><Link to="/">Home</Link></li>
              <li><Link to="/">About</Link></li>
              <li><Link to="/">Contact</Link></li>
              <li><Link to="/questions">Question</Link></li>
              <li><Link to="/register">Register</Link></li>
              <li><Link to="/profile">Profile</Link></li>
            </ul>
          </nav>

          {!isAuthenticated
            ? <Link to="/login" className="btn login-btn">Login</Link>
            : <button onClick={handleLogout} className="btn login-btn">Logout</button>}
        </div>
      </header>

      {/* ...continua com hero-section, services-section, etc */}
    </div>
  );
}
