import img1 from '@/assets/images/img1.jpeg';
import bgGreen from '@/assets/images/Background-green.jpg';
import CheckIco from '@/assets/images/ico/CheckIco.svg';
import UrlForm from '@/components/UrlForm/urlForm';
import { useNavigate } from 'react-router-dom';

import { useAuth } from '@/context/AuthContext';
import { submitVideo } from '@/services/videoService';

import '@/pages/Home/Home.css';

//determina o tipo de props necessário pelo typeScript
type SectionAProps = {
    setIsLoading: React.Dispatch<React.SetStateAction<boolean>>;
  };


export default function SectionA({setIsLoading}: SectionAProps) {

    const navigate = useNavigate(); //permite navegação de página
    const { isAuthenticated } = useAuth(); //logout removido(adicionar se necessário)

    async function onSubmit(url: string) {
        // Verifica se o usuário está logado
        if (!isAuthenticated) {
          navigate('/plans');
          return;
        }
    
        setIsLoading(true); //abre página de espera
    
        try {
          const result = await submitVideo(url);
          // Armazenando no localStorage(mudar para backend depois)
          localStorage.setItem('video_data', JSON.stringify(result));
    
          setIsLoading(false); //fecha página de espera
          navigate('/questions'); //encaminha para questions
        } catch (err: any) {
          console.error('Erro ao processar vídeo:', err);
          alert('Erro ao processar o vídeo. Verifique o link e tente novamente.');
          setIsLoading(false);
        }
      }

    return (
        <section className="hero-section" style={{ backgroundImage: `url(${bgGreen}')` }}>
        <div className="hero-grid">
          <div
            className="flex-[1_1_40%] bg-cover bg-center rounded-[5%]"
            style={{ backgroundImage: `url(${img1})` }}
          ></div>

          <div className="flex-[1_1_60%] text-white p-8">
            <h5 className="mb-4 font-normal text-white">O que nós fazemos</h5>
            <h1 className="mb-4 font-bold text-5xl">Resumos concisos de vídeo aulas do YouTube e questões interativas.</h1>
            <p className="mb-6 leading-relaxed">Cole o link, receba o resumo e pratique com questões. Simples, rápido e eficaz.
            </p>

            <ul className="grid grid-cols-2 gap-4 list-none mb-6 p-0">
              <li className="flex items-center"><img src={CheckIco} alt="" className="w-6 h-6 mr-2"/>Aprendizado</li>
              <li className="flex items-center"><img src={CheckIco} alt="" className="w-6 h-6 mr-2"/>Conhecimento</li>
              <li className="flex items-center"><img src={CheckIco} alt="" className="w-6 h-6 mr-2"/>Resumos</li>
              <li className="flex items-center"><img src={CheckIco} alt="" className="w-6 h-6 mr-2"/>Exercícios</li>
            </ul>
            
            <UrlForm onSubmit={onSubmit} />
          </div>
        </div>
      </section>
    );
}
