import { Link, useNavigate } from 'react-router-dom';
import BrainIco from '@/assets/images/ico/BrainIco.png';
import { useAuth } from '@/context/AuthContext';

export default function Header() {
  const { isAuthenticated, logout } = useAuth();
  const navigate = useNavigate();

  const handleLogout = () => {
    logout();
    navigate('/');
  };

  const scrollHome = () => {
    const el = document.getElementById('home');
    if (el) {
      el.scrollIntoView({ behavior: 'smooth' });
    }
  }

  return (
    <header className="header">
        <div className="header-container">
          <h3 className="logo">
            <img
              className="header-icon"
              src={BrainIco}
              alt="Icone"
              width={64}
              height={64}
            />
            <a href="/">Brainboost</a>
          </h3>

          <nav className="navbar">
            <ul>
              <li><Link to="/" onClick={() => scrollHome} >Home</Link></li>
              <li><Link to="/">About</Link></li>
              <li><Link to="/">Contact</Link></li>
              <li><Link to="/questions">Question</Link></li>
              <li><Link to="/register">Register</Link></li>
              <li><Link to="/profile">Profile</Link></li>
            </ul>
          </nav>
          {!isAuthenticated ? <Link to="/login" className="btn login-btn">Login</Link> : <button onClick={handleLogout} className="btn login-btn">Logout</button>}
          
        </div>
      </header>
  );
}
