//imagens
// import BrainIco from '@/assets/images/ico/BrainIco.png';
import img1 from '@/assets/images/img1.jpeg';
import bgGreen from '@/assets/images/Background-green.jpg';
import CheckIco from '@/assets/images/ico/CheckIco.svg';
import BookIco from '@/assets/images/ico/BookIco.png';
import BookPcIco from '@/assets/images/ico/BookPcIco.png';
import PcIco from '@/assets/images/ico/PcIco.png';

import img2 from '@/assets/images/img2.jpeg';
import img3 from '@/assets/images/img3.jpeg';
import img4 from '@/assets/images/img4.jpeg';
import img5 from '@/assets/images/img5.jpeg';
import waitUp from '@/assets/animations/waitup.gif';

//estilo de formatação
import '@/pages/Home/Home.css';

//dependências
import ScrollReveal from 'scrollreveal';
// import { Link } from 'react-router-dom';
import { useEffect } from 'react';
import { useState } from 'react';
import { useNavigate } from 'react-router-dom';

//serviços e contextos
import { useAuth } from '@/context/AuthContext';
import { submitVideo } from '@/services/videoService';
import UrlForm from '@/components/UrlForm/urlForm';

//componentes
import Header from '@/components/Header/header';

export default function Home() {

  // Definição dos estados
  const [isLoading, setIsLoading] = useState(false); //página de espera
  
  // Efeito de animação de scroll
  useEffect(() => {
    ScrollReveal().reveal('.header', {
      duration: 2000,
    });
    
    ScrollReveal().reveal('.hero-image', {
      origin: 'left',
      duration: 2000,
      distance: '10%',
    });
    
    ScrollReveal().reveal('.hero-content', {
      origin: 'right',
      duration: 2000,
      distance: '10%',
    });
  }, []);
  
  //funções
  const navigate = useNavigate();
  const { isAuthenticated } = useAuth(); //logout removido
  // const handleLogout = () => {
  //   logout();
  //   navigate('/');
  // };

  async function onSubmit(url: string) {
    // Verifica se o usuário está logado
    if (!isAuthenticated) {
      navigate('/plans');
      return;
    }

    setIsLoading(true); //abre página de espera

    try {
      const result = await submitVideo(url);
      // Armazenando no localStorage(mudar para backend depois)
      localStorage.setItem('video_data', JSON.stringify(result));

      setIsLoading(false); //fecha página de espera
      navigate('/questions'); //encaminha para questions
    } catch (err: any) {
      console.error('Erro ao processar vídeo:', err);
      alert('Erro ao processar o vídeo. Verifique o link e tente novamente.');
      setIsLoading(false);
    }
  }

  return (
    <div>
      {/* WAITING */}
      {isLoading && (
        <div className='waiting'>
          <img src={waitUp} alt="Carregando..." style={{ width: 100, height: 100 }} />
        </div>
      )}
      {/* HEADER */}
      <Header/>
      {/* <header className="header">
        <div className="header-container">
          <h3 className="logo">
            <img
              className="header-icon"
              src={BrainIco}
              alt="Icone"
              width={64}
              height={64}
            />
            <a href="/">Brainboost</a>
          </h3>

          <nav className="navbar">
            <ul>
              <li><Link to="/">Home</Link></li>
              <li><Link to="/">About</Link></li>
              <li><Link to="/">Contact</Link></li>
              <li><Link to="/questions">Question</Link></li>
              <li><Link to="/register">Register</Link></li>
              <li><Link to="/profile">Profile</Link></li>
            </ul>
          </nav>
          {!isAuthenticated ? <Link to="/login" className="btn login-btn">Login</Link> : <button onClick={handleLogout} className="btn login-btn">Logout</button>}

        </div>
      </header> */}

      <section className="hero-section" style={{ backgroundImage: `url(${bgGreen}')` }}>
        <div className="hero-grid">
          <div
            className="hero-image"
            style={{ backgroundImage: `url(${img1})` }}
          ></div>

          <div className="hero-content">
            <h5>O que nós fazemos</h5>
            <h1>Resumos concisos de vídeo aulas do YouTube e questões interativas.</h1>
            <p>Cole o link, receba o resumo e pratique com questões. Simples, rápido e eficaz.
            </p>

            <ul className="hero-list">
              <li><img src={CheckIco} alt="" />Aprendizado</li>
              <li><img src={CheckIco} alt="" />Conhecimento</li>
              <li><img src={CheckIco} alt="" />Resumos</li>
              <li><img src={CheckIco} alt="" />Exercícios</li>
            </ul>

            {/* <form action="#" method="POST" className="search-form">
              <input
                type="url"
                name="videoURL"
                placeholder="Enter a valid YouTube URL"
                required
              />
              <button type="submit" onClick={handleSubmit}>Pesquisar</button>
            </form> */}
            <UrlForm onSubmit={onSubmit} />
          </div>
        </div>
      </section>

      <section className="services-section">
        <div className="services-container">
          <h2>Transforme sua experiência de aprendizado e alcance novos patamares</h2>

          <div className="services-list">
            <div
              className="service-card"
              style={{ backgroundImage: `url(${img2})` }}
            >
              <img src={BookIco} alt="" className="card-icon" />
              <h4>Resumos</h4>
              <p>Videoaulas viram resumos rápidos e objetivos.</p>
            </div>

            <div
              className="service-card"
              style={{ backgroundImage: `url(${img3})` }}
            >
              <img src={BookPcIco} alt="" className="card-icon" />
              <h4>Estratégia</h4>
              <p>Usar NLP para resumir vídeos e gerar prática personalizada.</p>
            </div>

            <div
              className="service-card"
              style={{ backgroundImage: `url(${img4})` }}
            >
              <img src={BookIco} alt="" className="card-icon" />
              <h4>Missão</h4>
              <p>Tornar o estudo mais eficiente com resumos e questões automáticas.</p>
            </div>

            <div
              className="service-card"
              style={{ backgroundImage: `url(${img5})` }}
            >
              <img src={PcIco} alt="" className="card-icon" />
              <h4>Aprendizado</h4>
              <p>Conteúdo é fixado com revisão ativa e inteligente.</p>
            </div>
          </div>
        </div>
      </section>

      <section className="strategy-section">
        <div className="strategy-container">
          <div className="strategy-text">
            <h2>Estratégias e Soluções</h2>
            <ul className="strategy-list">
