import { useEffect, useState } from 'react';
import styles from '../styles/Questions.module.css';
import { useAuth } from './Context/AuthContext';
import { useNavigate } from 'react-router-dom';
import BrainIco from '../images/ico/BrainIco.png';

type Question = {
  id_question: string;
  texto_questao: string;
  pontuacao: number;
  alternatives: string[];
  correct_answer: string;
};

type VideoData = {
  id_video: string;
  link_video: string;
  resumo_video: string;
  questions: Question[];
};

export default function Questions() {
  const { isAuthenticated, logout } = useAuth();
  const navigate = useNavigate();
  const [videoData, setVideoData] = useState<VideoData | null>(null);
  const [currentIndex, setCurrentIndex] = useState(0);
  const [selectedAnswer, setSelectedAnswer] = useState<string | null>(null);

  useEffect(() => {
    const saved = localStorage.getItem('video_data');
    if (saved) {
      const parsed = JSON.parse(saved) as VideoData;
      setVideoData(parsed);
    } else {
      navigate('/'); // redireciona se não houver dados
    }
  }, [navigate]);

  const handleLogout = () => {
    logout();
    navigate('/');
  };

  const currentQuestion = videoData?.questions[currentIndex];

  const handleSelect = (answer: string) => {
    setSelectedAnswer(answer);
  };

  const handleNext = () => {
    setSelectedAnswer(null);
    setCurrentIndex((prev) => Math.min(prev + 1, (videoData?.questions.length || 1) - 1));
  };

  const handleBack = () => {
    setSelectedAnswer(null);
    setCurrentIndex((prev) => Math.max(prev - 1, 0));
  };

  if (!videoData || !currentQuestion) {
    return <div className={styles.pageWrapper}>Carregando...</div>;
  }

  return (
    <div className={styles.pageWrapper}>
      <header className={styles.header}>
        <div className={styles.logoArea}>
          <img src={BrainIco} alt="Ícone Brainboost" className={styles.headerIcon} />
          <h1 className={styles.logo}><a href="/">Brainboost</a></h1>
        </div>
        {isAuthenticated && <button onClick={handleLogout} className={styles.loginBtn}>Logout</button>}
      </header>

      <main className={styles.mainContent}>
        <div className={styles.container}>
          <div className={styles.pergunta}>
            <p><strong>{currentIndex + 1}.</strong> {currentQuestion.texto_questao}</p>
          </div>

          <div className={styles.respostas}>
            {currentQuestion.alternatives.map((alt, idx) => (
              <button
                key={idx}
                className={`${styles.rButton} ${selectedAnswer === alt ? styles.selected : ''}`}
                onClick={() => handleSelect(alt)}
              >
                {String.fromCharCode(65 + idx)}. {alt}
              </button>
            ))}
          </div>

          {selectedAnswer && (
            <div className={styles.feedback}>
              {selectedAnswer === currentQuestion.correct_answer
                ? '✅ Resposta correta!'
                : `❌ Resposta incorreta. A certa é: ${currentQuestion.correct_answer}`}
            </div>
          )}

          <div className={styles.navegacao}>
            <button className={styles.navButton} onClick={handleBack} disabled={currentIndex === 0}>
              ⬅ back
            </button>
            <button
              className={styles.navButton}
              onClick={handleNext}
              disabled={currentIndex === (videoData.questions.length - 1)}
            >
              next ➡
            </button>
          </div>
        </div>
      </main>
    </div>
  );
}
