import { useState, FormEvent } from 'react';
import { useNavigate } from 'react-router-dom';
import { useAuth } from '@/context/AuthContext';
import { registerUser } from '@/services/authService';
import arrowBack from '@/assets/images/ico/arrowBack.svg';

export default function Register() {
    const [fullName, setFullName] = useState('');
    const [username, setUsername] = useState('');
    const [password, setPassword] = useState('');
    const [error, setError] = useState('');

    const navigate = useNavigate();
    const { login } = useAuth();

    const handleSubmit = async (e: FormEvent<HTMLFormElement>) => {
        e.preventDefault();
        setError('');

        try {
            const res = await registerUser(fullName, username, password);
            login(res.data.access_token);
            navigate('/');
        } catch (err: any) {
            console.error('Erro no registro:', err);
            if (err.response?.data?.detail) {
                setError(err.response.data.detail);
            } else {
                setError('Erro ao registrar. Tente novamente.');
            }
        }
    };

    const goBack = () => {
        window.history.back();
    };

    return (
        <div className="min-h-screen bg-[#f9f9f9] flex items-center justify-center px-4">
            <div className="w-full max-w-md bg-white rounded-3xl shadow-xl p-8 relative">
                {/* Ícone de voltar */}
                <button onClick={goBack} className="absolute top-4 left-4 text-2xl text-gray-500 hover:text-gray-700">
                    <img src={arrowBack} alt="voltar" width={30} height={30} />
                </button>

                {/* Título e descrição */}
                <div className="mb-8 text-center">
                    <h2 className="text-2xl font-bold">Sign Up</h2>
                    <p className="text-gray-500 mt-1">Login details</p>
                </div>

                {error && <div className="text-red-500 text-sm mb-4">{error}</div>}

                <form className="space-y-4" onSubmit={handleSubmit}>
                    <input
                        required
                        type="text"
                        name="fullName"
                        placeholder="Full Name"
                        value={fullName}
                        onChange={(e) => setFullName(e.target.value)}
                        className="w-full h-12 px-4 border border-gray-200 rounded-full focus:outline-none focus:ring-2 focus:ring-green-500"
                    />
                    <input
                        required
                        type="text"
                        name="username"
                        placeholder="Username"
                        value={username}
                        onChange={(e) => setUsername(e.target.value)}
                        className="w-full h-12 px-4 border border-gray-200 rounded-full focus:outline-none focus:ring-2 focus:ring-green-500"
                    />
                    <input
                        required
                        type="password"
                        name="password"
                        placeholder="Password"
                        value={password}
                        onChange={(e) => setPassword(e.target.value)}
                        className="w-full h-12 px-4 border border-gray-200 rounded-full focus:outline-none focus:ring-2 focus:ring-green-500"
                    />

                    <button
                        type="submit"
                        className="w-full h-12 bg-green-600 text-white font-semibold rounded-full shadow-md hover:bg-green-700 transition"
                    >
                        Sign Up
                    </button>
                </form>
            </div>
        </div>
    );
}
