  //   });
    
  //   ScrollReveal().reveal('.hero-content', {
  //     origin: 'right',
  //     duration: 2000,
  //     distance: '10%',
  //   });
  // }, []);

  return (
    <div>
      {/* <Waiting isLoading={isLoading} /> Componente de espera */}
      {/* <Header/> */}
      {/* <SectionA setIsLoading={setIsLoading} /> */}
      {/* <SectionB/> */}
      <SectionC/>
      {/* <Footer/> */}
    </div>
  );
}
