//https://www.youtube.com/watch?v=WUH6V3hXWp8
import { useEffect, useState } from 'react';
import styles from '@/pages/Questions/Questions.module.css';
import { useAuth } from '@/context/AuthContext';
import { useNavigate } from 'react-router-dom';
import { useData } from '@/context/DataContext';
import Header from '@/components/Header/header';

export default function Questions() {
  const { token, user } = useAuth();
  const navigate = useNavigate();
  const [currentIndex, setCurrentIndex] = useState(0);
  const [selectedAnswer, setSelectedAnswer] = useState<string | null>(null);
  const [score, setScore] = useState(0);
  const [finished, setFinished] = useState(false);
  const { videoData } = useData();
  const IS_SIMULATING = videoData?.id_video === "fake_video_id";


  useEffect(() => {
    if (!videoData) {
      navigate('/');
    }
  }, [videoData, navigate]);

  const currentQuestion = videoData?.questions[currentIndex];

  const handleSelect = (answer: string) => {
    setSelectedAnswer(answer);

    if (videoData) {
      videoData.questions[currentIndex].userAnswer = answer;
    }

    if (answer === currentQuestion?.correct_answer) {
      setScore((prev) => prev + 1);
    }
  };

  const submitAnswers = async () => {
    if (!videoData || !user || !token) return;

    const payload = {
      id_user: user.id,
      id_video: videoData.id_video,
      answers: videoData.questions.map((q) => ({
        id_question: q.id_question,
        answer: q.userAnswer || '',
      })),
    };

    try {
      const response = await fetch(`${import.meta.env.VITE_API_URL}/submit_answers/`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          Authorization: `Bearer ${token}`,
        },
        body: JSON.stringify(payload),
      });

      if (!response.ok) {
        throw new Error('Erro ao enviar respostas');
      }

      const data = await response.json();
      console.log('Pontuação salva com sucesso:', data);
    } catch (error) {
      console.error('Erro no envio das respostas:', error);
    }
  };

  const handleNext = async () => {
    if (!videoData) return;

    const nextIndex = currentIndex + 1;

    if (nextIndex >= videoData.questions.length) {
      if (!IS_SIMULATING) {
        await submitAnswers();
      }
      setFinished(true);
    } else {
      setCurrentIndex(nextIndex);
      setSelectedAnswer(null);
    }
  };

  const handleBack = () => {
    if (currentIndex === 0) return;
    setCurrentIndex(currentIndex - 1);
    setSelectedAnswer(null);
  };

  const handleRestart = () => {
    setCurrentIndex(0);
    setScore(0);
    setSelectedAnswer(null);
    setFinished(false);
  };

  if (!videoData) return <div className={styles.pageWrapper}>Carregando...</div>;

  return (
    <div className="min-h-screen bg-white">
  <Header navBar={false} />

  <main className="max-w-3xl mx-auto px-4 py-8">
    <div className="bg-white p-6 shadow rounded-lg">
      {finished ? (
        <>
          <h2 className="text-2xl font-bold mb-4">Quiz finalizado!</h2>
          <p className="text-lg mb-6">Sua pontuação: {score} / {videoData.questions.length}</p>
          <div className="flex gap-4">
            <button
              className="px-4 py-2 bg-blue-600 text-white rounded hover:bg-blue-700"
              onClick={handleRestart}
            >
              Reiniciar
            </button>
            <button
              className="px-4 py-2 bg-gray-600 text-white rounded hover:bg-gray-700"
              onClick={() => navigate('/')}
            >
              Voltar para Home
            </button>
          </div>
        </>
      ) : (
        <>
          <div className="mb-6">
            <p className="text-lg font-medium">
              <strong>{currentIndex + 1}.</strong> {currentQuestion?.texto_questao}
            </p>
          </div>

          <div className="space-y-3 mb-6">
            {currentQuestion?.alternatives.map((alt, idx) => {
              const isCorrect = alt === currentQuestion?.correct_answer;
              const isSelected = alt === selectedAnswer;

              let className = "w-full text-left px-4 py-2 border rounded focus:outline-none";
              if (selectedAnswer) {
                className += isCorrect
                  ? ' bg-green-100 border-green-500 text-green-800'
                  : isSelected
                  ? ' bg-red-100 border-red-500 text-red-800'
                  : ' bg-gray-100';
              } else {
                className += ' hover:bg-gray-50';
              }

              return (
                <button
                  key={idx}
                  className={className}
                  onClick={() => !selectedAnswer && handleSelect(alt)}
                  disabled={!!selectedAnswer}
                >
                  {String.fromCharCode(65 + idx)}. {alt}
                </button>
              );
            })}
          </div>

          {selectedAnswer && (
            <div className="mb-6 p-3 rounded bg-yellow-100 text-yellow-800">
              {selectedAnswer === currentQuestion?.correct_answer
                ? '✅ Resposta correta!'
                : `❌ Resposta incorreta. Correta: ${currentQuestion?.correct_answer}`}
            </div>
          )}

          <div className="flex justify-between gap-4">
            <button
              className="px-4 py-2 bg-gray-400 text-white rounded hover:bg-gray-500"
              onClick={handleBack}
              disabled={currentIndex === 0}
            >
              ⬅ Voltar
            </button>
            <button
              className="px-4 py-2 bg-blue-600 text-white rounded hover:bg-blue-700"
              onClick={handleNext}
              disabled={!selectedAnswer}
            >
              {currentIndex === videoData.questions.length - 1 ? 'Finalizar' : 'Próxima ➡'}
            </button>
          </div>
        </>
      )}
    </div>
  </main>
</div>

  );
}
